import './App.css';
import { SetS3Config } from "./services";
import { withAuthenticator, AmplifySignOut } from '@aws-amplify/ui-react';
import Storage from "@aws-amplify/storage";
import { Component } from "react";
import Predictions from "./Predictions"
import { Auth } from 'aws-amplify';
import { Toolbar, Typography, AppBar } from '@material-ui/core';
import { Button } from 'reactstrap';

class App extends Component {

  state = {
    imageName: "",
    imageFile: "",
    userID : "",
    response: ""
  };

    async componentDidMount() {
    //await (await Auth.currentCredentials()) // Wait for credentials

    const info = await Auth.currentUserInfo()
    console.log('Returned info: ', info['attributes']['sub'])
    //this.setState({ info })
     this.setState({ userID : info['attributes']['sub']})
  }

  async signOut() {
    try {
        await Auth.signOut({ global: true });
    } catch (error) {
        console.log('error signing out: ', error);
    }
}

  uploadImage = () => {
    SetS3Config("intern-project-web-tier-uploads", "private");
    Storage.put(`${this.state.userID}/user-files/${this.upload.files[0].name}`,
                this.upload.files[0],
                { contentType: this.upload.files[0].type })
      .then(result => {
        this.upload = null;
        this.setState({ response: "Success uploading file!" });
      })
      .catch(err => {
        this.setState({ response: `Cannot uploading file: ${err}` });
      });
  };

  render() {
    return (

      <div className="App">
      <Toolbar>
        <Typography variant="h6">
          Protein Annotation
        </Typography>
         <AmplifySignOut />
      </Toolbar>
        <h2>S3 Upload example...</h2>
        <input
          type="file"
          //accept="image/png, image/jpeg"
          style={{ display: "none" }}
          ref={ref => (this.upload = ref)}
          onChange={e =>
            this.setState({
              imageFile: this.upload.files[0],
              imageName: this.upload.files[0].name
            })
          }
        />
        <input value={this.state.imageName} placeholder="Select file" />
        <button
          onClick={e => {
            this.upload.value = null;
            this.upload.click();
          }}
          loading={this.state.uploading}
        >
          Browse
        </button>

        <button onClick={this.uploadImage}> Upload File </button>

        {!!this.state.response && <div>{this.state.response}</div>}

          <div>
            <Predictions />
          </div>
      </div>
    );
  }
}

export default withAuthenticator(App);
//export default App;
