import './App.css';
import { SetS3Config } from "./services";
import { withAuthenticator, AmplifySignOut } from '@aws-amplify/ui-react';
import Storage from "@aws-amplify/storage";
import { Component } from "react";
import Predictions from "./Predictions";
import { Auth } from 'aws-amplify';
import { Toolbar } from '@material-ui/core';

class App extends Component {

  state = {
    fileName: "",
    inputFile: "",
    userID : "",
    response: ""
  };

  async componentDidMount() {

    const info = await Auth.currentUserInfo();
    console.log('Returned info: ', info['attributes']['sub']);
    //this.setState({ info });
     this.setState({ userID : info['attributes']['sub']});
  }

  uploadFile = () => {
    SetS3Config("intern-project-web-tier-uploads", "private");
    Storage.put(`${this.state.userID}/user-files/${this.upload.files[0].name}`,
                this.upload.files[0],
                { contentType: this.upload.files[0].type })
      .then(result => {
        this.upload = null;
        this.setState({ response: "Success uploading file!" });
        window.location.reload(true);
      })
      .catch(err => {
        this.setState({ response: `Cannot uploading file: ${err}` });
      });
  };

  render() {
    return (
      <div className="App">
        <Toolbar>
         <AmplifySignOut />
        </Toolbar>

        <h1>Protein Annotation</h1>

        <h3>Please upload your file...</h3>
          <input
            type="file"
            //accept="image/png, image/jpeg"
            style={{ display: "none" }}
            ref={ref => (this.upload = ref)}
            onChange={e =>
              this.setState({
                inputFile: this.upload.files[0],
                fileName: this.upload.files[0].name
              })
            }
          />
        <input value={this.state.fileName} placeholder="Select file" />
        <button
          onClick={e => {
            this.upload.value = null;
            this.upload.click();
          }}
          loading={this.state.uploading}
        >
          Browse
        </button>

        <button onClick={this.uploadFile}> Upload File </button>

        {!!this.state.response && <div>{this.state.response}</div>}

        <div>
          <Predictions />
        </div>
      </div>
    );
  }
}

export default withAuthenticator(App);
//export default App;
